import React from 'react';
import { useParams } from 'react-router-dom';
import equipments from '../data/data.jsx'; 
import Operation from './Operation';
import './item.css';

const ItemPage = () => {
  const { itemId } = useParams();
  const item = equipments.find((item) => item.id === parseInt(itemId));

  if (!item) {
    return <p>Item not found</p>;
  }

  return (
    <div className="item_section">
        {/* Блок для фото */}
        <div className="item_view">
            <img src={item.photo} alt={item.title} className="item-image" />
        </div>

        {/* Блок для опису */}
        <div className="item-details">
            <div className="item-description">
                <h1>{item.title}</h1>
                <p>{item.description}</p>
                <p>Category: {item.category}</p>
                <p>Brand: {item.brand}</p>
            </div>
        </div>

        {/* Використання компоненти Operation */}
        <Operation price={item.price} />
    </div>
  );
};

export default ItemPage;
