import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { fetchEquipmentById } from '/Users/nataliafetych/Downloads/web-lab-10/my-app/src/api.js';
import Loader from '../loader/loader.jsx';
import './item.css';

const ItemPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [equipment, setEquipment] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const data = await fetchEquipmentById(id);
        setEquipment(data);
      } catch (err) {
        console.error('Error loading item data:', err.message);
        setError('Failed to load item data');
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [id]);

  if (loading) return <Loader />;
  if (error) return <p>Error: {error}</p>;
  if (!equipment) return <p>No equipment found.</p>;

  return (
    <div className="item-page">
      <img src={equipment.image} alt={equipment.title} className="item-image" />
      <div className="item-details">
        <h1>{equipment.title}</h1>
        <p><strong>Description:</strong> {equipment.description}</p>
        <p><strong>Price:</strong> {equipment.price} â‚¬</p>
        <p><strong>Brand:</strong> {equipment.brand}</p>
        <p><strong>Category:</strong> {equipment.category}</p>
        <button onClick={() => navigate(-1)}>Go Back</button>
      </div>
    </div>
  );
};

export default ItemPage;
