import React, { useState } from 'react';
import './filter.css';

const FilterBar = ({ filters, onApplyFilters }) => {
  const [localFilters, setLocalFilters] = useState(filters);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setLocalFilters((prevFilters) => ({
      ...prevFilters,
      [name]: value,
    }));
  };

  const handleApply = () => {
    onApplyFilters(localFilters);
  };

  return (
    <div className="filter-bar">
      <select name="category" value={localFilters.category} onChange={handleChange}>
        <option value="">All Categories</option>
        <option value="electronics">Electronics</option>
        <option value="furniture">Furniture</option>
        <option value="clothing">Clothing</option>
      </select>

      <select name="brand" value={localFilters.brand} onChange={handleChange}>
        <option value="">All Brands</option>
        <option value="brand1">Brand 1</option>
        <option value="brand2">Brand 2</option>
        <option value="brand3">Brand 3</option>
      </select>

      <select name="priceRange" value={localFilters.priceRange} onChange={handleChange}>
        <option value="">All Prices</option>
        <option value="0-50">0-50 €</option>
        <option value="50-100">50-100 €</option>
        <option value="100-500">100-500 €</option>
      </select>

      <button onClick={handleApply}>Apply Filters</button>
    </div>
  );
};

export default FilterBar;
