import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { fetchEquipments } from '../server/api.js';
import Operation from '../operation/operation.jsx';
import './item.css';

const ItemPage = () => {
  const { itemId } = useParams();
  const [item, setItem] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchItem = async () => {
      setLoading(true);
      setError(null);
      try {
        const equipments = await fetchEquipments();
        const foundItem = equipments.find((equipment) => equipment.id === parseInt(itemId));
        if (!foundItem) throw new Error('Item not found');
        setItem(foundItem);
      } catch (err) {
        setError(err.message || 'Failed to fetch item');
      } finally {
        setLoading(false);
      }
    };
    fetchItem();
  }, [itemId]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="item-section">
      <div className="item-view">
        <img src={item.photo} alt={item.title} className="item-image" />
      </div>
      <div className="item-details">
        <div className="details-content">
          <h1 id="name-element">{item.title}</h1>
          <p className="item-description">{item.description}</p>
          <p className="item-category">Category: <span>{item.category}</span></p>
          <p className="item-brand">Brand: <span>{item.brand}</span></p>
          <div className="price-and-buttons">
            <p className="price">{item.price} â‚¬</p>
            <Operation price={item.price} />
          </div>
        </div>
      </div>
    </div>
  );
};

export default ItemPage;
