import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import FilterBar from '../filter/filter.jsx';
import Loader from '../loader/loader.jsx';
import './catalog.css';

const CatalogPage = ({ searchQuery }) => {
  const [filters, setFilters] = useState({ category: '', priceRange: '', brand: '' });
  const [equipments, setEquipments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchEquipments = async () => {
    try {
      setLoading(true);
      setError(null);

      let query = `http://localhost:3002/equipments`;
      const params = new URLSearchParams();

      if (filters.category) params.append('category', filters.category);
      if (filters.brand) params.append('brand', filters.brand);
      if (filters.priceRange) {
        const [minPrice, maxPrice] = filters.priceRange.split('-').map(Number);
        params.append('minPrice', minPrice);
        params.append('maxPrice', maxPrice);
      }
      if (searchQuery) params.append('searchQuery', searchQuery);

      if (params.toString()) query += `?${params.toString()}`;

      const response = await fetch(query);
      if (!response.ok) throw new Error(`HTTP error! Status: ${response.status}`);
      const data = await response.json();
      setEquipments(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchEquipments();
  }, [filters, searchQuery]); // Виконуємо запит при зміні фільтрів або пошукового запиту

  const handleApplyFilters = (newFilters) => {
    // Оновлюємо глобальні фільтри, застосовуються після натискання кнопки
    setFilters(newFilters);
  };

  const groupItems = (items, groupSize) => {
    const rows = [];
    for (let i = 0; i < items.length; i += groupSize) {
      rows.push(items.slice(i, i + groupSize));
    }
    return rows;
  };

  const groupedItems = groupItems(equipments, 4);

  if (loading) return <Loader />;
  if (error) return <p>Error: {error}</p>;

  return (
    <div className="catalog-page">
      <FilterBar filters={filters} onApplyFilters={handleApplyFilters} />
      {groupedItems.map((group, groupIndex) => (
        <div key={`group-${groupIndex}`} className="image-row">
          {group.map((item) => (
            <div key={`item-${item.id}`} className="image-container">
              <img src={item.photo} alt={item.title} className="other-image" />
              <h3>{item.title}</h3>
              <p>{item.description}</p>
              <p>{item.price} €</p>
              <Link to={`/item/${item.id}`}>
                <button>View more</button>
              </Link>
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default CatalogPage;
