import React, { useState } from 'react';
import FilterBar from '../filter/filter.jsx';
import './catalog.css';

const CatalogPage = ({ searchQuery = '' }) => {
  const equipments = [
    { id: 1, title: "Backpack", category: "backpacks", price: 78.54, brand: "brand1", description: "Rollpack Proof 26 - Daypack", photo: "https://www.bfgcdn.com/1500_1500_90/502-6757-0211/aevor-rollpack-proof-26-daypack.jpg" },
    { id: 2, title: "Bottle", category: "bottles", price: 25.17, brand: "brand2", description: "Seeker 2L - Water bottle", photo: "https://www.bfgcdn.com/1500_1500_90/533-1342-0111/hydrapak-seeker-2l-water-bottle.jpg" },
    { id: 3, title: "Walking socks", category: "socks", price: 17.62, brand: "brand1", description: "Merino Wool Cushion Heavy Long Socks", photo: "https://www.bfgcdn.com/1500_1500_90/201-4883-0311/stoic-merino-wool-silk-hiking-socks-walking-socks.jpg" },
    { id: 4, title: "Walking socks", category: "socks", price: 14.68, brand: "brand3", description: "Merino Hiking Crew Socks", photo: "https://www.bfgcdn.com/1500_1500_90/201-3151-0711/stoic-merino-hiking-crew-socks-walking-socks.jpg" },
    { id: 5, title: "Pullover", category: "jackets", price: 73.08, brand: "brand2", description: "Merino260 StadjanSt. Zip Pullover", photo: "https://www.bfgcdn.com/1500_1500_90/005-3489-1311/stoic-merino260-stadjanst-hoody-merino-hoodie.jpg" },
    { id: 6, title: "Jacket", category: "jackets", price: 80.65, brand: "brand1", description: "Women's Merino260 StadjanSt. Jacket", photo: "https://www.bfgcdn.com/600_600_90/105-3172-0311/stoic-womens-merino260-stadjanst-jacket-merino-jacket.jpg" },
    { id: 7, title: "Backpack Padje Light 45", category: "backpacks", price: 196.61, brand: "brand3",  photo: "https://www.bfgcdn.com/600_600_90/502-7832-0411/lowe-alpine-airzone-trail-30-walking-backpack.jpg" },
    { id: 8, title: "Softshell jacket women's Madris Light ML Hooded", category: "jackets", price: 107.11 , brand: "brand1", photo: "https://www.bfgcdn.com/1500_1500_90/103-1819-0311/mammut-womens-madris-light-ml-hooded-jacket-softshell-jacket.jpg" },
  ];

  const [filters, setFilters] = useState({ category: '', priceRange: '', brand: '' });

  const handleApplyFilters = (newFilters) => {
    setFilters(newFilters);
  };

  const filterByPriceRange = (item, range) => {
    if (!range) return true;
    const [min, max] = range.split('-').map(Number);
    return item.price >= min && item.price <= max;
  };

  // Діагностика для пошуку
  console.log('Search Query:', searchQuery);
  console.log('Filters:', filters);

  const filteredEquipments = equipments.filter(item =>
    (!filters.category || item.category === filters.category) &&
    (!filters.brand || item.brand === filters.brand) &&
    filterByPriceRange(item, filters.priceRange) &&
    (searchQuery && typeof searchQuery === 'string' ? (
      item.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      item.description.toLowerCase().includes(searchQuery.toLowerCase())
    ) : true)
  );

  // Виводимо результати фільтрації для діагностики
  console.log('Filtered Equipments:', filteredEquipments);

  const groupItems = (items, groupSize) => {
    const rows = [];
    for (let i = 0; i < items.length; i += groupSize) {
      rows.push(items.slice(i, i + groupSize));
    }
    return rows;
  };

  const groupedItems = groupItems(filteredEquipments, 4);

  return (
    <div className="catalog-page">
      <FilterBar onApplyFilters={handleApplyFilters} />
      {groupedItems.length === 0 ? (
        <p>No items found.</p>
      ) : (
        groupedItems.map((group, groupIndex) => (
          <div key={`group-${groupIndex}`} className="image-row">
            {group.map(item => (
              <div key={`item-${item.id}`} className="image-container">
                <img src={item.photo} alt={item.title} className="other-image" />
                <h3>{item.title}</h3>
                <p>{item.description}</p>
                <p>{item.price} €</p>
                <button>Buy</button>
              </div>
            ))}
          </div>
        ))
      )}
    </div>
  );
};

export default CatalogPage;
