import React, { useState, useEffect } from 'react';
import { BrowserRouter, Route, Routes } from 'react-router-dom';
import Header from './components/header/header.jsx';
import Main_section from './components/main/main.jsx';
import Other_section from './components/other/other.jsx';
import Footer from './components/footer/footer.jsx';
import Catalog from './components/catalog/catalog.jsx';
import ItemPage from './components/item/item.jsx';
import './App.css';

function App() {
  const [searchQuery, setSearchQuery] = useState(''); // Стан для пошукового запиту
  const [equipments, setEquipments] = useState([]); // Стан для даних про обладнання

  // Використання useEffect для отримання даних з API
  useEffect(() => {
    const fetchEquipments = async () => {
      try {
        const response = await fetch("http://localhost:3020/equipments");
        if (!response.ok) throw new Error("Failed to fetch");
        const data = await response.json();
        console.log("Fetched Equipments:", data); // Лог отриманих даних
        setEquipments(data); // Збереження даних у стан
      } catch (error) {
        console.error("Error fetching equipments:", error); // Лог помилок
      }
    };

    fetchEquipments();
  }, []); // Виконання лише при першому завантаженні

  return (
    <BrowserRouter>
      <Header onSearch={setSearchQuery} />
      <Routes>
        <Route
          path="/"
          element={
            <>
              <Main_section />
              <Other_section />
            </>
          }
        />
        <Route
          path="/catalog"
          element={
            <Catalog searchQuery={searchQuery} equipments={equipments} />
          }
        />
        <Route
          path="/item/:itemId"
          element={<ItemPage />}
        />
      </Routes>
      <Footer />
    </BrowserRouter>
  );
}

export default App;
