import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import FilterBar from '../filter/filter.jsx';
import Loader from '../loader/loader.jsx';
import './catalog.css';

const CatalogPage = ({ searchQuery }) => {
  const [filters, setFilters] = useState({ category: '', priceRange: '', brand: '' });
  const [equipments, setEquipments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchEquipments = async () => {
    try {
      setLoading(true);
      setError(null);

      const query = `http://localhost:3020/equipments`;
      const params = {};

      if (filters.category) params.category = filters.category;
      if (filters.brand) params.brand = filters.brand;
      if (filters.priceRange) {
        const [minPrice, maxPrice] = filters.priceRange.split('-').map(Number);
        if (!isNaN(minPrice) && !isNaN(maxPrice)) {
          params.minPrice = minPrice;
          params.maxPrice = maxPrice;
        }
      }
      if (searchQuery) params.searchQuery = searchQuery;

      const response = await fetch(`${query}?${new URLSearchParams(params).toString()}`);
      const data = await response.json();
      setEquipments(data);
    } catch (err) {
      console.error('Error fetching data:', err.message);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchEquipments();
  }, [filters, searchQuery]);

  const handleApplyFilters = (newFilters) => {
    setFilters(newFilters);
  };

  const groupItems = (items, groupSize) => {
    const rows = [];
    for (let i = 0; i < items.length; i += groupSize) {
      rows.push(items.slice(i, i + groupSize));
    }
    return rows;
  };

  const groupedItems = groupItems(equipments, 4);

  if (loading) return <Loader />;
  if (error) return <p>Error: {error}</p>;

  return (
    <div className="catalog-page">
      <FilterBar filters={filters} onApplyFilters={handleApplyFilters} />
      {groupedItems.length === 0 ? (
        <p>No items found</p>
      ) : (
        groupedItems.map((group, groupIndex) => (
          <div key={`group-${groupIndex}`} className="image-row">
            {group.map((item) => (
              <div key={`item-${item.id}`} className="image-container">
                <img src={item.photo} alt={item.title} className="other-image" />
                <h3>{item.title}</h3>
                <p>{item.description}</p>
                <p>{item.price} â‚¬</p>
                <Link to={`/item/${item.id}`}>
                  <button>View more</button>
                </Link>
              </div>
            ))}
          </div>
        ))
      )}
    </div>
  );
};

export default CatalogPage;
