import axios from 'axios';

const API_BASE_URL = 'http://localhost:3020';

// Отримати всі записи з можливими фільтрами
export const fetchEquipments = async (filters = {}) => {
  try {
    const response = await axios.get(`${API_BASE_URL}/equipments`, { params: filters });
    return response.data;
  } catch (error) {
    console.error('Error fetching equipments:', error.message);
    throw error;
  }
};

// Отримати конкретний запис за ID
export const fetchEquipmentById = async (id) => {
  try {
    const response = await axios.get(`${API_BASE_URL}/equipments/${id}`);
    return response.data;
  } catch (error) {
    console.error('Error fetching equipment by ID:', error.message);
    throw error;
  }
};

// Додати новий запис
export const createEquipment = async (equipment) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/equipments`, equipment);
    return response.data;
  } catch (error) {
    console.error('Error creating equipment:', error.message);
    throw error;
  }
};

// Оновити запис за ID
export const updateEquipment = async (id, updates) => {
  try {
    const response = await axios.put(`${API_BASE_URL}/equipments/${id}`, updates);
    return response.data;
  } catch (error) {
    console.error('Error updating equipment:', error.message);
    throw error;
  }
};

// Видалити запис за ID
export const deleteEquipment = async (id) => {
  try {
    const response = await axios.delete(`${API_BASE_URL}/equipments/${id}`);
    return response.data;
  } catch (error) {
    console.error('Error deleting equipment:', error.message);
    throw error;
  }
};
