import React from 'react';
import { useParams, Link } from 'react-router-dom';
import operations from '../operations/operations.jsx';
import equipments from '../data/data.jsx'; 
import './item.css';

const ItemPage = () => {
  const { itemId } = useParams();
  const item = equipments.find((item) => item.id === parseInt(itemId));

  if (!item) {
    return <p>Item not found</p>;
  }

  return (
    <div className="item_section">
        {/* Блок для фото */}
        <div className="item_view">
            <img src={item.photo} alt={item.title} className="item-image" />
        </div>

        {/* Блок для опису */}
        <div className="item-details">
            <div className="item-description">
                <h1>{item.title}</h1>
                <p>{item.description}</p>
                <p>Category: {item.category}</p>
                <p>Brand: {item.brand}</p>
            </div>
        </div>

        {/* Блок для ціни і кнопок */}
        <div className="price-and-buttons">
            <p className="price">Price: {item.price} €</p>
            <Link to="/catalog">
                <button>Go back</button>
            </Link>
            <Link to="/cart">
                <button>Add to cart</button>
            </Link>
        </div>
    </div>
  );
};

export default ItemPage;
