import React, { useState } from 'react';
import './filter.css';

const FilterBar = ({ filters, onApplyFilters }) => {
  // Локальний стан для збереження вибраних фільтрів
  const [localFilters, setLocalFilters] = useState(filters);

  const handleCategoryChange = (event) => {
    setLocalFilters((prev) => ({
      ...prev,
      category: event.target.value,
    }));
  };

  const handlePriceRangeChange = (event) => {
    setLocalFilters((prev) => ({
      ...prev,
      priceRange: event.target.value,
    }));
  };

  const handleBrandChange = (event) => {
    setLocalFilters((prev) => ({
      ...prev,
      brand: event.target.value,
    }));
  };

  const handleApplyFilters = () => {
    // Передаємо локальні фільтри у `CatalogPage`
    onApplyFilters(localFilters);
  };

  return (
    <section id="filter_section">
      <div className="filter-bar">
        <select
          className="filter-select"
          value={localFilters.category}
          onChange={handleCategoryChange}
        >
          <option value="">Category</option>
          <option value="backpacks">Backpacks</option>
          <option value="bottles">Bottles</option>
          <option value="socks">Socks</option>
          <option value="jackets">Jackets</option>
        </select>

        <select
          className="filter-select"
          value={localFilters.priceRange}
          onChange={handlePriceRangeChange}
        >
          <option value="">Price Range</option>
          <option value="0-20">€0 - €20</option>
          <option value="20-50">€20 - €50</option>
          <option value="50-100">€50 - €100</option>
          <option value="100-200">€100 - €200</option>
        </select>

        <select
          className="filter-select"
          value={localFilters.brand}
          onChange={handleBrandChange}
        >
          <option value="">Brand</option>
          <option value="brand1">Brand 1</option>
          <option value="brand2">Brand 2</option>
          <option value="brand3">Brand 3</option>
        </select>
      </div>
      <div className="second_part">
        <button className="filter-button" onClick={handleApplyFilters}>
          Apply Filters
        </button>
      </div>
    </section>
  );
};

export default FilterBar;
