import React, { useState } from 'react';
import './other.css'; 
import ViewButton from '../view_button/view_button.jsx';

const Other_section = () => {
  const [visibleCount, setVisibleCount] = useState(3);

  const showMoreItems = () => {
    setVisibleCount(prevCount => prevCount + 3);
  };

  const items = [
    { src: "/img/IMG_2937.JPG", alt: "Tent", name: "Tent" },
    { src: "/img/IMG_2938.JPG", alt: "Backpack", name: "Backpack" },
    { src: "/img/IMG_2939.JPG", alt: "Row", name: "Row" },
  ];

  return (
    <section id="other_s">
      <h2 id="more_information">Here are some products from our shop</h2>
      <div className="image-row">
        {items.slice(0, visibleCount).map((item, index) => (
          <div key={index} className="image-containerr">
            <img src={item.src} alt={item.alt} className="other-immage" />
            <p className='el_name'>{item.name}</p>
          </div>
        ))}
      </div>

      {visibleCount < items.length && (
        <ViewButton onClick={showMoreItems} />
      )}

      <div className="image_container_fog">
        <img src="/img/IMG_2202.jpg" alt="placeholder" className="imagee" />
      </div>
    </section>
  );
};

export default Other_section;
