import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import FilterBar from '../filter/filter.jsx';
import { fetchEquipments } from './';
import './catalog.css';

const CatalogPage = ({ searchQuery }) => {
  const [filters, setFilters] = useState({ category: '', priceRange: '', brand: '' });
  const [equipments, setEquipments] = useState([]);
  const [loading, setLoading] = useState(true);

  const handleApplyFilters = (newFilters) => {
    setFilters(newFilters);
  };

  const fetchData = async () => {
    setLoading(true);
    try {
      const data = await fetchEquipments(filters);
      setEquipments(data);
    } catch (error) {
      console.error('Error loading equipments:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, [filters]);

  const filterByPriceRange = (item, range) => {
    if (!range) return true;
    const [min, max] = range.split('-').map(Number);
    return item.price >= min && item.price <= max;
  };

  const filteredEquipments = equipments.filter(item =>
    filterByPriceRange(item, filters.priceRange) &&
    (searchQuery ? item.title.toLowerCase().includes(searchQuery.toLowerCase()) : true)
  );

  const groupItems = (items, groupSize) => {
    const rows = [];
    for (let i = 0; i < items.length; i += groupSize) {
      rows.push(items.slice(i, i + groupSize));
    }
    return rows;
  };

  const groupedItems = groupItems(filteredEquipments, 4);

  return (
    <div className="catalog-page">
      <FilterBar onApplyFilters={handleApplyFilters} />
      {loading ? (
        <p>Loading...</p>
      ) : (
        groupedItems.map((group, groupIndex) => (
          <div key={`group-${groupIndex}`} className="image-row">
            {group.map(item => (
              <div key={`item-${item.id}`} className="image-container">
                <img src={item.photo} alt={item.title} className="other-image" />
                <h3>{item.title}</h3>
                <p>{item.description}</p>
                <p>{item.price} â‚¬</p>
                <Link to={`/item/${item.id}`}>
                  <button>View more</button>
                </Link>
              </div>
            ))}
          </div>
        ))
      )}
    </div>
  );
};

export default CatalogPage;
