import React, { useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import equipments from '../data/data.jsx'; 
import Operation from '../operation/operation.jsx';
import './item.css';

const ItemPage = () => {
  const { itemId } = useParams();
  const [searchTerm, setSearchTerm] = useState("");

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  // Фільтруємо товари за пошуковим запитом
  const filteredItems = equipments.filter((equipment) =>
    equipment.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    equipment.description.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Якщо є itemId, знайти конкретний товар
  const item = itemId ? equipments.find((item) => item.id === parseInt(itemId)) : null;

  if (itemId && !item) {
    return <p>Item not found</p>;
  }

  return (
    <>
      {/* Поле пошуку */}
      <div className="search-bar">
        <input 
          type="text"
          placeholder="Search items..."
          value={searchTerm}
          onChange={handleSearch}
        />
      </div>

      {/* Відображення результатів пошуку */}
      <div className="search-results">
        {filteredItems.map((filteredItem) => (
          <div key={filteredItem.id} className="search-item">
            <Link to={`/item/${filteredItem.id}`}>
              <h3>{filteredItem.title}</h3>
            </Link>
            <p>{filteredItem.description}</p>
          </div>
        ))}
      </div>

      {/* Якщо є itemId, показати конкретний товар */}
      {itemId && item && (
        <div className="item_section">
          <div className="item_view">
            <img src={item.photo} alt={item.title} className="item-image" />
          </div>

          <div className="item-details">
            <div className="naming_element">
              <h1 id='name_element'>{item.title}</h1>
              <p>{item.description}</p>
              <p>Category: {item.category}</p>
              <p>Brand: {item.brand}</p>
            </div>
          </div>
        </div>
      )}

      {item && <Operation price={item.price} />}
    </>
  );
};

export default ItemPage;
