import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Operation from '../operation/operation.jsx';
import Loader from '../loader/loader.jsx';
import axios from 'axios';
import './item.css';

const ItemPage = () => {
  const { itemId } = useParams(); 
  const [item, setItem] = useState(null); 
  const [loading, setLoading] = useState(true); 
  const [error, setError] = useState(null); 

  const fetchItem = async () => {
    try {
      setLoading(true);
      setError(null);

      const { data } = await axios.get(`http://localhost:3020/equipments/${itemId}`);
      console.log('Отримано елемент:', data); 
      setItem(data);
    } catch (err) {
      console.error('Помилка завантаження елемента:', err.message);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchItem();
  }, [itemId]);

  if (loading) return <Loader />;
  if (error) return <p>Error: {error}</p>;
  if (!item) return <p>Item not found</p>;

  return (
    <>
      <div className="item_section">
        <div className="item_view">
          <img src={item.photo} alt={item.title} className="item-image" />
        </div>
        <div className="item-details">
          <div className="naming_element">
            <h1 id="name_element">{item.title}</h1>
            <p>{item.description}</p>
            <p>Category: {item.category}</p>
            <p>Brand: {item.brand}</p>
          </div>
        </div>
      </div>
      <Operation price={item.price} />
    </>
  );
};

export default ItemPage;
