import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import FilterBar from '../filter/filter.jsx';
import equipments from '../data/data.jsx'; 
import './catalog.css';

const CatalogPage = ({ searchQuery }) => {
  const [filters, setFilters] = useState({ category: '', priceRange: '', brand: '' });

  const handleApplyFilters = (newFilters) => {
    setFilters(newFilters);
  };

  const filterByPriceRange = (item, range) => {
    if (!range) return true;
    const [min, max] = range.split('-').map(Number);
    return item.price >= min && item.price <= max;
  };

  const filteredEquipments = equipments.filter(item =>
    (!filters.category || item.category === filters.category) &&
    (!filters.brand || item.brand === filters.brand) &&
    filterByPriceRange(item, filters.priceRange) &&
    (searchQuery ? item.title.toLowerCase().includes(searchQuery.toLowerCase()) : true)
  );

  return (
    <div className="catalog-page">
      <FilterBar onApplyFilters={handleApplyFilters} />
      {filteredEquipments.length > 0 ? (
        filteredEquipments.map(item => (
          <div key={item.id} className="image-container">
            <img src={item.photo} alt={item.title} className="other-image" />
            <h3>{item.title}</h3>
            <p>{item.description}</p>
            <p>{item.price} â‚¬</p>
            <Link to={`/item/${item.id}`}>
              <button>View more</button>
            </Link>
          </div>
        ))
      ) : (
        <p>No results found.</p>
      )}
    </div>
  );
};

export default CatalogPage;
