import React, { useState } from 'react';
import './filter.css';

const FilterBar = ({ onFilterChange }) => {
  const [category, setCategory] = useState('');
  const [priceRange, setPriceRange] = useState('');
  const [brand, setBrand] = useState('');

  const handleFilterChange = () => {
    onFilterChange({ category, priceRange, brand });
  };

  return (
    <section id="filter_section">
      <div className="filter-bar">
        <select className="filter-select" value={category} onChange={(e) => setCategory(e.target.value)}>
          <option value="">Category</option>
          <option value="backpacks">Backpacks</option>
          <option value="bottles">Bottles</option>
          <option value="socks">Socks</option>
          <option value="jackets">Jackets</option>
        </select>
        <select className="filter-select" value={priceRange} onChange={(e) => setPriceRange(e.target.value)}>
          <option value="">Price Range</option>
          <option value="0-20">€0 - €20</option>
          <option value="20-50">€20 - €50</option>
          <option value="50-100">€50 - €100</option>
          <option value="100-200">€100 - €200</option>
        </select>
        <select className="filter-select" value={brand} onChange={(e) => setBrand(e.target.value)}>
          <option value="">Brand</option>
          <option value="brand1">Brand 1</option>
          <option value="brand2">Brand 2</option>
          <option value="brand3">Brand 3</option>
        </select>
      </div>
      <div className='second_part'>
        <button className="filter-button" onClick={handleFilterChange}>Apply Filters</button>
      </div>
    </section>
  );
};

export default FilterBar;
